// main.c DA2PmodTest	ECE3622// Dennis Silage   c2019#include "DA2Pmod.h"#include "xparameters.h"#include "xil_io.h"#include "xil_printf.h"#include <stdio.h>#include <math.h>#include <stdint.h>#include <limits.h>//DAC2Pmod from Address Editor in Vivado#define DA2acq  0x43C00000   //DA2 acquisition    - output#define DA2dav	0x43C00004	 //DA2 data available - input#define DA2dat1	0x43C00008	 //DA2 channel 1 data - output#define DA2dat2 0x43C0000C	 //DA2 channel 2 data - outputconst int LUTSIZE = 4096;inline void SineLUT(int16_t  SINLUT[LUTSIZE]);int main(void){    int16_t  SINLUT[LUTSIZE]; //DAC SINE LUT ARRAY DEFINITION    SineLUT(SINLUT);	//FILL DAC SINE LUT    int dacdata=0;	//LUT index    int dacdav;		//DAC data available    int dacacq=0;	//DAC acquire    Xil_Out32(DA2acq,0);    while(1)    {        if(dacdata==4096)            dacdata=0;        dacdav=Xil_In32(DA2dav);        if(dacdav==0 && dacacq==0)			//DAC not in use?        {            Xil_Out32(DA2dat1, SINLUT[dacdata]);	//output DAC data            Xil_Out32(DA2dat2, SINLUT[dacdata]);	//output DAC data            Xil_Out32(DA2acq,1);			//DAC acquire            dacacq=1;        }        if(dacdav==1 && dacacq==1)			//DAC data available        {            Xil_Out32(DA2acq,0);			//stop DAC acquire            dacacq=0;            dacdata++;						//increment DAC data        }    }}void SineLUT(int16_t  SINLUT[LUTSIZE]) {    for (int i = 0; i <= 4096; i++)    {        SINLUT[i] = (int16_t) 2000*sinf(2.0f * M_PI * ((float) i / LUTSIZE))+2092;        printf ("%d\n", SINLUT[i]);    }}